import React, { useState } from 'react';
import axios from 'axios';
import './GenerateImageForm.css';

const apiUrl = process.env.REACT_APP_API_URL;

function GenerateImageForm() {
  const [input, setInput] = useState('');
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${apiUrl}/generate-image`, JSON.parse(input), {
        headers: {
          'Content-Type': 'application/json'
        },
        responseType: 'blob'
      });
      if (response.status === 200) {
        const url = URL.createObjectURL(new Blob([response.data]));
        setImage(url);
      } else {
        const errorMessage = await response.data.text();
        console.error('Error generating image:', errorMessage);
        setMessage(`Error: ${response.data.error} (Status: ${response.data.status_code})`);
      }
    } catch (error) {
      console.error('Error generating image:', error);
      setMessage('An error occurred while generating the image.');
    }
  };

  const handleTest = async () => {
    try {
      const response = await axios.get(`${apiUrl}/test`);
      setMessage(response.data.message);
    } catch (error) {
      console.error('Error testing Flask:', error);
      setMessage('An error occurred while testing Flask.');
    }
  };

  return (
    <div className="generate-image-form">
      <form onSubmit={handleSubmit}>
        <textarea value={input} onChange={handleChange} rows="10" cols="50" placeholder="Enter JSON body here"></textarea>
        <button type="submit">Generate Image</button>
      </form>
      {image && <img src={image} alt="Generated" />}
      <button onClick={handleTest}>Test Flask</button>
      {message && <p>{message}</p>}
    </div>
  );
}

export default GenerateImageForm;
