// src/components/BackgroundChanger.js
import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';

const backgrounds = {
  '/': 'url(./assets/home-background.jpg)',
  '/about': 'url(./assets/about-background.jpg)',
  '/contact': 'url(./assets/contact-background.jpg)',
  '/products': 'url(./assets/products-background.jpg)',
  '/blog': 'url(./assets/blog-background.jpg)',
};

function BackgroundChanger() {
  const location = useLocation();

  useEffect(() => {
    document.body.style.backgroundImage = backgrounds[location.pathname] || backgrounds['/'];
  }, [location]);

  return null;
}

export default BackgroundChanger;
